using System.Collections; using System.Collections.Generic; using UnityEngine;  public class ShipControl : MonoBehaviour {       public float translationpeed;    // Use this for initialization  void Start () {             }      // Update is called once per frame     void Update()     {          this.transform.Translate(0, 0, translationpeed);          //Up         if (Input.GetKey(KeyCode.UpArrow))             this.transform.Translate(Vector3.up);          //Down         if (Input.GetKey(KeyCode.DownArrow))             this.transform.Translate(Vector3.down);           //Right         if (Input.GetKey(KeyCode.RightArrow))             this.transform.Translate(Vector3.right);           //Left         if (Input.GetKey(KeyCode.LeftArrow))             this.transform.Translate(Vector3.left);          //Space         if (Input.GetKeyDown(KeyCode.Space))         {              //Creamos un GameObject a partir de un prefab             GameObject newMissile = GameObject.Instantiate(Resources.Load("Bullets/Missile") as GameObject);              //Buscar el GameObject del pivote de disparo (ShootPivot)             GameObject shootPivot = GameObject.Find("Ship/ShootPivot");              //Hace Misil hijo de ShootPivot             newMissile.transform.SetParent(shootPivot.transform);              //Mover el Misil a la posició de ShootPivot             newMissile.transform.localPosition = Vector3.zero;              //Sacar el Misil del Pivote para que no se mueva con el avion             newMissile.transform.SetParent(null);          }          } //End Update()      }  